import { Component } from '@angular/core';
import { IonicModule, ModalController } from '@ionic/angular';
import { CameraPreviewOptions, CameraPreviewPictureOptions , CameraPreview} from '@capacitor-community/camera-preview';
import { BrowserModule } from '@angular/platform-browser';
import { PreviewPage } from '../preview/preview.page';


@Component({
  selector: 'app-tab1',
  templateUrl: 'tab1.page.html',
  styleUrls: ['tab1.page.scss'],
  standalone: true,
  imports: [IonicModule,   BrowserModule,

    ],
})
export class Tab1Page {
  image: any;
  cameraActive = false;
  constructor(private modal: ModalController) {}

  ngOnInit() {
    this.startCameraPreview();
 }

 async startCameraPreview() {
  const cameraPreviewOptions: CameraPreviewOptions = {
    position: 'rear',
    parent: 'cameraPreview',
    className: 'cameraPreview'
  };

  await CameraPreview.start(cameraPreviewOptions);
}
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Tab 1
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Tab 1</ion-title>
    </ion-toolbar>
  </ion-header>

  <div id="cameraPreview"></div>

  <ion-button (click)="openCamera()" color="primary" expand="block" fill="solid" size="default">
    Open Camera
  </ion-button>

</ion-content>
