import { Component } from '@angular/core';
import { IonicModule, ModalController } from '@ionic/angular';
import { CameraPreviewOptions, CameraPreviewPictureOptions , CameraPreview} from '@capacitor-community/camera-preview';
import { BrowserModule } from '@angular/platform-browser';
import { PreviewPage } from '../preview/preview.page';


@Component({
  selector: 'app-tab1',
  templateUrl: 'tab1.page.html',
  styleUrls: ['tab1.page.scss'],
  standalone: true,
  imports: [IonicModule,   BrowserModule,

    ],
})
export class Tab1Page {
  image: any;
  cameraActive = false;
  constructor(private modal: ModalController) {}

  ngOnInit() {
    this.launchCamera()
 }

 launchCamera() {
     const cameraPreviewOptions: CameraPreviewOptions = {
       position: 'front', // front or rear
       parent: 'content', // the id on the ion-content
       className: '',
       width: window.screen.width, //width of the camera display
       height: window.screen.height - 200, //height of the camera
       toBack: false,
     };
     CameraPreview.start(cameraPreviewOptions);
     this.cameraActive = true;
 }
  async takePicture() {
    const cameraPreviewPictureOptions: CameraPreviewPictureOptions = {
      quality: 90
    };
    const result = await CameraPreview.capture(cameraPreviewPictureOptions);
    this.image = `data:image/jpeg;base64,${result.value}`;
    this.stopCamera();
  }
 async stopCamera() {
    await CameraPreview.stop();
    this.modal.dismiss(this.image);
  }

  async flipCamera() {
    await CameraPreview.flip();
 }
}
