<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Tab 1</ion-title>
    </ion-toolbar>
  </ion-header>

  <div *ngIf="image">
    <img [src]="image" alt="" srcset="">
  </div>
  <ion-button (click)="openCamera()" color="primary" expand="block" fill="solid" size="default">
    Open Camera
  </ion-button>

</ion-content>
